import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../App';
import { mockAuth } from '../services/api';

export default function Login(){
  const [name, setName] = useState('');
  const [role, setRole] = useState('student');
  const { setAuth } = useContext(AuthContext);
  const nav = useNavigate();

  const submit = async (e) =>{
    e.preventDefault();
    const user = await mockAuth({name, role}); // mock API
    setAuth(user);
    localStorage.setItem('auth', JSON.stringify(user));
    if(role==='student') nav('/student');
    else if(role==='teacher') nav('/teacher');
    else nav('/admin');
  };

  return (
    <div className="max-w-md mx-auto p-4 bg-white rounded shadow dark:bg-gray-800">
      <h2 className="text-xl font-semibold mb-2">Login</h2>
      <form onSubmit={submit} className="space-y-3">
        <input value={name} onChange={e=>setName(e.target.value)} placeholder="Your name" className="w-full border px-2 py-1 rounded" required />
        <select value={role} onChange={e=>setRole(e.target.value)} className="w-full border px-2 py-1 rounded">
          <option value="student">Student</option>
          <option value="teacher">Teacher</option>
          <option value="admin">Admin</option>
        </select>
        <button type="submit" className="w-full bg-blue-600 text-white px-3 py-2 rounded">Login as {role}</button>
      </form>
    </div>
  );
}
