import React, { useContext } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { LangContext, AuthContext } from '../App';

export default function Navbar({ setDark, dark }){
  const { lang, setLang } = useContext(LangContext);
  const { auth, setAuth } = useContext(AuthContext);
  const loc = useLocation();
  const nav = useNavigate();
  const logout = ()=>{ setAuth(null); localStorage.removeItem('auth'); nav('/login'); };

  return (
    <nav className="bg-blue-600 text-white">
      <div className="max-w-6xl mx-auto flex items-center justify-between p-4">
        <div className="flex items-center gap-3">
          <div className="font-bold text-lg">Digital Pathshala</div>
        </div>
        <div className="flex items-center gap-3">
          <Link className={'px-3 py-1 rounded '+(loc.pathname==='/'? 'bg-blue-800':'' )} to="/">Home</Link>
          <Link className={'px-3 py-1 rounded '+(loc.pathname==='/about'? 'bg-blue-800':'' )} to="/about">About</Link>
          {auth ? (
            <>
              {auth.role==='student' && <Link className={'px-3 py-1 rounded '+(loc.pathname==='/student'? 'bg-blue-800':'' )} to="/student">Student</Link>}
              {auth.role==='teacher' && <Link className={'px-3 py-1 rounded '+(loc.pathname==='/teacher'? 'bg-blue-800':'' )} to="/teacher">Teacher</Link>}
              {auth.role==='admin' && <Link className={'px-3 py-1 rounded '+(loc.pathname==='/admin'? 'bg-blue-800':'' )} to="/admin">Admin</Link>}
              <button onClick={logout} className="px-3 py-1 bg-white text-black rounded">Logout</button>
            </>
          ) : (
            <Link className={'px-3 py-1 rounded '+(loc.pathname==='/login'? 'bg-blue-800':'' )} to="/login">Login</Link>
          )}
          <select className="text-black rounded px-2 py-1" value={lang} onChange={e=>setLang(e.target.value)}>
            <option value="en">EN</option>
            <option value="hi">हिन्दी</option>
            <option value="pa">ਪੰਜਾਬੀ</option>
          </select>
          <button onClick={()=>setDark(d=>!d)} className="bg-white text-black px-2 py-1 rounded">{dark? 'Light':'Dark'}</button>
        </div>
      </div>
    </nav>
  );
}
