import React, { useContext } from 'react';
import { LangContext } from '../App';
import { STR } from '../utils/languageData';

export default function Home() {
  const { lang } = useContext(LangContext);
  const t = STR[lang];

  return (
    <main className="min-h-screen bg-white dark:bg-black">  {/* page background here */}
      <div className="max-w-6xl mx-auto p-4">
        <h1 className="text-3xl font-bold mb-2">{t.appTitle}</h1>
        <p className="text-gray-700 dark:text-gray-300 mb-4">{t.welcome}</p>

        <div className="grid md:grid-cols-2 gap-4">
          <div className="p-4 bg-white/90 dark:bg-white/5 rounded shadow border border-gray-200 dark:border-gray-700">
            <h3 className="font-semibold mb-2">{t.home_problem}</h3>
            <p className="text-sm">{t.home_ps_text}</p>
          </div>

          <div className="p-4 bg-white/90 dark:bg-white/5 rounded shadow border border-gray-200 dark:border-gray-700">
            <h3 className="font-semibold mb-2">{t.home_expected}</h3>
            <ul className="list-disc pl-5 text-sm">
              <li>{t.home_point_offline}</li>
              <li>{t.home_point_locale}</li>
              <li>{t.home_point_dash}</li>
            </ul>
          </div>
        </div>
      </div>
    </main>
  );
}
