import React, { useContext } from 'react';
import { LangContext } from '../App';
import { STR } from '../utils/languageData';

export default function Home(){
  const { lang } = useContext(LangContext);
  const t = STR[lang];
  return (
    <section>
      <h1 className="text-3xl font-bold mb-2">{t.appTitle}</h1>
      <p className="text-gray-700 dark:text-gray-300 mb-4">{t.welcome}</p>
      <div className="grid md:grid-cols-2 gap-4">
        <div className="p-4 bg-white rounded shadow dark:bg-gray-800">
          <h3 className="font-semibold mb-2">Problem Statement</h3>
          <p className="text-sm">Many government schools in Nabha lack updated computer infrastructure and reliable internet.</p>
        </div>
        <div className="p-4 bg-white rounded shadow dark:bg-gray-800">
          <h3 className="font-semibold mb-2">Expected Outcomes</h3>
          <ul className="list-disc pl-5 text-sm">
            <li>Offline-capable lessons</li>
            <li>Local language content</li>
            <li>Teacher dashboards</li>
          </ul>
        </div>
      </div>
    </section>
  );
}
